@keyframes scaler {
  0% {
    transform: scale(0.89, 0.89);
  }
  50% {
    transform: scale(1, 1);
  }
  100% {
    transform: scale(0.89, 0.89);
  }
}
@keyframes heart-move {
  0% {
    -webkit-transform: translate(0px, 0px) rotate(0deg);
    transform: translate(0px, 0px) rotate(0deg);
  }
  20% {
    -webkit-transform: translate(-13px, 1px) rotate(-6deg);
    transform: translate(-13px, 1px) rotate(-6deg);
  }
  40% {
    -webkit-transform: translate(-41px, -22px) rotate(-22deg);
    transform: translate(-41px, -22px) rotate(-22deg);
  }
  60% {
    -webkit-transform: translate(-33px, -22px) rotate(-50deg);
    transform: translate(-33px, -22px) rotate(-50deg);
  }
  80% {
    -webkit-transform: translate(40px, -72px) rotate(-50deg);
    transform: translate(40px, -72px) rotate(-50deg);
  }
  100% {
    -webkit-transform: translate(0px, 0px) rotate(0deg);
    transform: translate(0px, 0px) rotate(0deg);
  }
}
@keyframes like-bumb {
  0% {
    transform: scale(0.8, 0.8);
  }

  50% {
    transform: scale(1, 1);
  }
  100% {
    transform: scale(0.8, 0.8);
  }
}
.student-search-container {
  width: 100vw;
  background: var(--theme-deafult);
  .content-container {
    display: flex;
    flex: wrap;
    width: 80%;
    margin: auto;
    .student-content {
      width: 67%;
      flex: 0 0 auto;
      margin: 80px 0 80px 0;
      .content-title {
        width: 70%;
      }
      .motto {
        font-size: 0.8rem;
      }
      .job-choice-container {
        margin-top: 8px;
        span {
          margin-right: 16px;
          color: white;
          font-size: 0.7rem;
        }
      }
    }
    .image-container {
      position: relative;
      align-content: center;
      height: 340px;
      max-width: 290px;
      width: 33%;
      flex: 0 0 auto;
      display: flex;
      margin: 30px;
      .box-1 {
        width: 60%;
        position: relative;
        margin-left: -10.4%;
        margin-top: -14%;
        margin-right: -6%;
        flex-grow: 1;
        img {
          width: 160px;
          margin-top: 12px;
          margin-right: 30px;
          border-radius: 120px;
          position: absolute;
        }
        .circle-container {
          animation: scaler 4s infinite ease-in-out alternate;
          top: -10px;
          right: 23px;
          position: absolute;
          .inlay-circle-1 {
            width: 180px;
            height: 180px;
            border: solid transparent 4px;
            border-right: solid #10d1ef 4px;
            border-radius: 50%;
            transform: rotate(-45deg);
          }
        }
      }
      .box-2 {
        width: 40%;
        position: relative;
        margin-left: 0%;
        margin-top: 19.4%;
        margin-right: -11%;
        flex-grow: 1;
        background: rgb(232, 231, 231, 0.5);
        border-radius: 120px;
        img {
          position: absolute;
          height: 100%;
          width: auto;
          margin-left: -60px;
        }
        .circle-container {
          animation: scaler 4s infinite ease-in-out alternate;
          bottom: -14px;
          right: -14px;;
          position: absolute;
          .inlay-circle-2 {
            width: 180px;
            height: 180px;
            border: solid transparent 4px;
            border-right: solid #ffc766 4px;
            border-radius: 50%;
            transform: rotate(45deg);
          }
        }
      }
      .heart-icon {
        position: absolute;
        bottom: 6%;
        left: 26%;
        animation: heart-move 7s infinite ease-in-out alternate;
      }
      .like-icon {
        position: absolute;
        top: 10.4%;
        left: 100%;
        z-index: 10;
        animation: like-bumb 4s infinite ease-in-out alternate;
      }
    }
  }
}

.job-lists-container {
  text-align: center;
  width: 80%;
  margin: auto;
  .sub-header {
    color: rgb(109, 108, 108);
    font-size: 1rem;
  }
}
.our-past-work-container {
  width: 80%;
  margin: auto;
  .our-past-work {
    width: 100%;
    display: flex;
    .image-container {
      width: 50%;
      img {
        width: 100%;
        object-fit: contain;
        object-position: right;
      }
    }
    .content-container {
      width: 50%;
      padding: 50px 0 50px 50px;
    }
  }
  .wrap-count {
    text-align: center;
  }
}
